package com.amzi.dao;

public interface LoginDao {
	boolean validate(String name, String pass);
}

package com.amzi.dao;

public class LoginDaoBean implements LoginDao {

	@Override
	public boolean validate(String name, String pass) {

		if( name != null && pass != null && name.equals("Admin") && pass.equals("passw0rd") ) {
			return true;
		}

		return false;
	}

}

package com.amzi.servlets;

import java.io.IOException;
import java.io.PrintWriter;

import javax.servlet.RequestDispatcher;
import javax.servlet.ServletException;
import javax.servlet.http.HttpServlet;
import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;

import com.amzi.dao.LoginDao;
import com.amzi.dao.LoginDaoBean;

public class LoginServlet extends HttpServlet{

	private static final long serialVersionUID = 1L;

	public void doPost(HttpServletRequest request, HttpServletResponse response)  
			throws ServletException, IOException {  
		LoginDao loginDao = new LoginDaoBean();

		response.setContentType("text/html");  
		PrintWriter out = response.getWriter();  

		String n=request.getParameter("username");  
		String p=request.getParameter("userpass"); 

		HttpSession session = request.getSession(false);
		if(session!=null)
		session.setAttribute("name", n);

		if(loginDao.validate(n, p)){  
			RequestDispatcher rd=request.getRequestDispatcher("welcome.jsp");  
			rd.forward(request,response);  
		}  
		else{  
			out.print("<p style=\"color:red\">Sorry username or password error</p>");  
			RequestDispatcher rd=request.getRequestDispatcher("index.jsp");  
			rd.include(request,response);  
		}  

		out.close();  
	}  
}
